#include <iostream>#include "aql-driver.h"int main (int argc, char **argv) {	AQL::aql_driver driver;	for (++argv; argv[0]; ++argv)		if (*argv == std::string("-p"))			driver.trace_parsing = true;		else if (*argv == std::string("-s"))			driver.trace_scanning = true;		else if (!driver.parse(*argv))		{					std::cout << "Erro na compilacao do programa: " << std::endl;			std::cout << driver.result << std::endl;		}}